def verify_similarity(question, similar_question):
    # Set your OpenAI API key as an environment variable

    # Define a prompt template
    prompt_template = """You are an advanced NLP AI, and your job is to identify whether two questions have the same meaning and context interms of stock market.
                      Simply return true or false. If two questions have same years return true,if one question have year and another question doesnt have year return false.
                      q1: {question} 
                      q2: {similar_question}"""

    # Create a prompt template with input variables
    prompt = PromptTemplate(template=prompt_template, input_variables=["question", "similar_question"])
    
    # Create an LLMChain object
    llm_chain = LLMChain(prompt=prompt, llm=ChatOpenAI(temperature=0, model="gpt-4o-mini"))

    result = llm_chain.predict(question=question, similar_question=similar_question).strip().lower()  # Convert to lowercase
    print(result)
    return result # Return True if the result is "true", otherwise False



def query_similar_questions(question,collection_name):
# Query the collection to find similar questions
    # collection = client.get_collection(name="response_cache")
    collection = client.get_or_create_collection(name=collection_name)
    count = collection.count()
    if count ==0:
        return None
    else:
        result = collection.query(
            query_texts=[question],
            n_results=1
        )
        print(result)
    
        # # Check if 'ids' is empty, if so, return None immediately
        # if not result['ids']:
        #     return None

        # Check if 'distances' is empty, if so, return None immediately
        if not result['distances'] or not result['distances'][0]:
            return None

        # Extract the distance value (similarity score)
        distance = result['distances'][0][0]

        # Check if the question is similar based on the distance threshold
        if distance < 0.2:
            # Get the most similar question from the result
            similar_question = str(result['documents'][0][0])
            print(f"in function similar: {similar_question}")
            
            # Check if the questions are truly similar using the verify_similarity function
            is_similar = verify_similarity(question, similar_question)
            print(f"cache hit: {is_similar}")
            
            # If they are similar, return the similar question
            if is_similar=='true':
                return similar_question
            else:
                #If not similar or verification failed, return None
                return None



